def handle_count(twitter_handle)
    return twitter_handle.length

  end
  
  def shortest_handle(twitter_handle)
    shortest = twitter_handle[0] #on commence au premier élement de la liste
    twitter_handle.each do |handle| #permet de parcourir tous les éléments de la liste twitter_handles, et à chaque itération, l'élément en cours est stocké dans la variable handle.
      shortest = handle if handle.length < shortest.length
    end
    return shortest
end 
def count_handles_with_5_characters(twitter_handle)
    count = twitter_handle.count { |handle| handle.length == 6 } #J'ai mis 6 au lieu de 5 pour prendre en compte le "@"
    return count
end
def count_handles_with_majuscule(twitter_handle)
    count = 0 #on part avec un compte de 0
    twitter_handle.each do |handle|
      count += 1 if handle[1].match?(/[A-Z]/) #on ajoute un compte de 1, si majuscule présente au 2ème charactère
    end
    return count
end
 def sort_by_alphabetical(twitter_handle)
    twitter_sort_by_alphabetical = twitter_handle.sort
    return twitter_sort_by_alphabetical
end 
def sort_by_length(twitter_handle)
    twitter_sort_by_length = twitter_handle.sort_by { |handle| handle.length}
    return twitter_sort_by_length
end
def find_epenser_position(twitter_handle, epenser)
    position = twitter_handle.index("@epenser")
    return position
end
def handle_length_distribution(twitter_handle)
    distribution = Hash.new(0)
    twitter_handle.each { |handle| distribution[handle.length] += 1 }
    return distribution
  end
  
